// REACT IMPORTS
import React, { useState, useEffect } from "react";
import axios from "axios";

// COMPONENT IMPORTS
import Header from "./components/Header/Header";
import SearchBar from "./components/SearchBar/SearchBar";
import Notes from "./components/Notes/Notes.js";
import Footer from "./components/Footer/Footer";

// CSS STYLES
import classes from "./App.module.css";

function App() {
  const [notesList, setNotesList] = useState([]);
  const [input, setInput] = useState("");

  useEffect(() => {
    axios
      .get(`/api/read`)
      .then((res) => {
        console.log(res);
        setNotesList(res.data.notes);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  // function handling input state
  const inputStateHandler = (data) => {
    setInput(data.target.value);
  };

  // function handling the create note button
  const createNoteHandler = () => {
    if (input === "") {
      return;
    }

    // adding note in the database
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    axios
      .post(`/api/add`)
      .then((data) => {
        console.log(data);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  return (
    <>
      <Header />
      <div className={classes.App}>
        <h2>So what's on your mind ?</h2>
        <SearchBar
          query={input}
          stateHandler={inputStateHandler}
          buttonHandler={createNoteHandler}
        />
        {notesList.length !== 0 ? (
          <Notes notes={notesList} />
        ) : (
          <div className={classes.Loader}>Loading...</div>
        )}
      </div>
      {/* <Footer /> */}
    </>
  );
}

export default App;
